import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { fetchTokens } from '../redux/actions';

class Login extends React.Component {
  state = {
    nome: '',
    email: '',
  };

  onHandleChange = ({ target: { id, value } }) => {
    this.setState({
      [id]: value,
    });
  };

  submitHandler = (event) => {
    event.preventDefault();
    const { getToken, history } = this.props;
    getToken();
    history.push('/game');
  };

  playButtonHandler = () => {
    const { nome, email } = this.state;
    const tamanhoDoNome = nome.length;
    const tamanhoDoEmail = email.length;

    return !(tamanhoDoNome && tamanhoDoEmail);
  };

  render() {
    const { nome, email } = this.state;
    const { history } = this.props;
    return (
      <section>
        <form
          className="form-login"
          onSubmit={ this.submitHandler }
        >
          <h1>Login</h1>
          <label htmlFor="nome">
            <input
              id="nome"
              type="text"
              placeholder="Digite seu nome"
              value={ nome }
              onChange={ this.onHandleChange }
              data-testid="input-player-name"
            />
          </label>
          <label htmlFor="email">
            <input
              id="email"
              type="email"
              placeholder="Digite seu Email"
              value={ email }
              onChange={ this.onHandleChange }
              data-testid="input-gravatar-email"
            />
          </label>
          <button
            type="submit"
            disabled={ this.playButtonHandler() }
            data-testid="btn-play"
          >
            Play
          </button>
        </form>
        <button
          data-testid="btn-settings"
          type="button"
          onClick={ () => history.push('/settings') }
        >
          Configurações
        </button>
      </section>
    );
  }
}

Login.propTypes = {
  getToken: PropTypes.func.isRequired,
  history: PropTypes.shape().isRequired,
};

const mapDispatchToProps = (dispatch) => ({
  getToken: () => dispatch(fetchTokens()),
});

export default connect(null, mapDispatchToProps)(Login);
