const INITIAL_STATE = {
  token: '',
  player: { name: '',
    assertions: 0,
    score: 0,
    gravatarEmail: '' },
};

function player(state = INITIAL_STATE, action) {
  switch (action.type) {
  case 'REQUEST_TOKEN':
    return ({
      ...state,
      token: action.payload.token });
  default:
    return state;
  }
}

export default player;
